import Theme from '../styles/theme';
import { useState } from 'react';
import { submitContact } from '../services/apiservice';
import { useScroll } from '../components/hooks';

function Contact() {

    const [inputs, setInputs] = useState({
        fullname: '',
        email: '',
        cellphone: '',
        request: ''
    })
    const [loading, setLoading] = useState(false)
    const newscroll = useScroll()
    const [formStatus, setFormStatus] = useState('fillingout')

    const handleType = (e) => {
        e.persist()
        setInputs({...inputs, [e.target.name]: e.target.value})
    }

    const handleContact = async(e) => {
        e.preventDefault()
        setLoading(true)
        const submitForm = await submitContact(inputs.fullname, inputs.request, inputs.email, inputs.cellphone)
        if(submitForm) {
            console.log(submitForm)
            setLoading(false)
            setFormStatus('succeeded')
        }
    }

    const clearForm = (e) => {
        e.persist()
        setFormStatus('fillingout')
        setInputs({
            fullname: '',
            email: '',
            cellphone: '',
            request: ''
        })
    }

    return (
        <div className='body'>
            <div className='content'>
                <div className='column'>
                    <img className='loader' src='/loading_a.gif'/>
                    <form className='contactform' onSubmit={handleContact}>
                        <img className='banner' src='/burke_wide_title_black.png' />
                        <h2>Contact Us Today!</h2>
                        <label>Full Name</label>
                        <input 
                            onChange={handleType}
                            name='fullname'
                            value={inputs.fullname}
                            required
                        />
                        <label>Email</label>
                        <input 
                            onChange={handleType}
                            name='email'
                            value={inputs.email}
                            required
                        />
                        <label>Cellphone</label>
                        <input 
                            onChange={handleType}
                            name='cellphone'
                            type='tel'
                            value={inputs.cellphone}
                            required
                        />
                        <label>What can we help you with</label>
                        <textarea 
                            onChange={handleType}
                            name='request'
                            value={inputs.request}
                            required
                        />
                        <button type='submit'>Contact Us</button>
                    </form>
                    <div className='msgcenter'>
                        <p>Thank you for your input, Burke Developing will get back to you as soon as possible!</p>
                        <div className='next' onClick={clearForm}>Fill out form again</div>
                    </div>
                </div>
                <div className='cinfo'>
                    <h1>Contact Information</h1>
                    <ul>
                        <li><img src='/icons/icon_boss.png'/>Daniel Burke</li>
                        <li><img src='/icons/icon_phone.png'/>1-781-426-1894</li>
                        <li><img src='/icons/icon_email.png'/>dan@burkedeveloping.com</li>
                    </ul>
                </div>
            </div>
            <style jsx>{`
                .msgcenter {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    width: 50%;
                    padding: 10px;
                    background: white;
                    transform: translate(-50%,-50%);
                    display: ${formStatus == 'succeeded' ? 'block': 'none'};
                }
                .msgcenter p {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    text-align: center;
                    font: 14px ${Theme.fonts.title};
                }
                .next {
                    float: left;
                    padding: 10px;
                    background: ${Theme.colors.gunmetal};
                    color: white;
                    margin-left: 50%;
                    font: 12px 'Roboto';
                    cursor: pointer;
                    transform: translateX(-50%);
                }
                .cinfo ul {
                    float: left;
                    width: 90%;
                    background: ${Theme.colors.gunmetal};
                    padding: 20px 5%;
                    transition: all .5s ease;
                    transform: perspective(200px) translate3d(0,0,10px);
                    margin-top: ${newscroll.scrollY + 50}px;
                }
                .cinfo ul li {
                    float: left;
                    width: 100%;
                    margin: 10px 0;
                    list-style: none;
                    color: white;
                    font: 16px ${Theme.fonts.subheader};
                }
                .cinfo ul li img {
                    width: 20px;
                    height: 20px;
                    margin-right: 15px;
                    margin-bottom: -6px;
                }
                .cinfo {
                    width: 35%;
                    float: left;
                    min-height: 400px;
                    max-height: 900px;
                    margin: 10px 1%;
                    padding: 10px 1%;
                    border-radius: 8px;
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .cinfo h1 {
                    float: left;
                    width: 95%;
                    padding: 10px 2.5%;
                    margin: 0;
                    font: 20px 'Montserrat';
                }
                .banner {
                    float: left;
                    width: 80%;
                    margin: -40px 10%;

                }
                .loader {
                    position: absolute;
                    top: 70%;
                    left: 50%;
                    transform: translate(-50%,-50%);
                    width: 25px;
                    height: 25px;
                    display: ${loading == true ? 'block': 'none'};
                }
                .contactform button {
                    float: left;
                    padding: 14px 5%;
                    background: ${Theme.colors.charcoal};
                    color: white;
                    font: 14px 'Roboto';
                    margin: 20px 0;
                    border: none;
                    cursor: pointer;
                    border-radius: 8px;
                    display: ${loading == true ? 'none': 'block'};
                }
                .contactform h2 {
                    float: left;
                    width: 95%;
                    padding: 10px 2.5%;
                    margin: 23px 0;
                    border-bottom: 1px solid ${Theme.colors.gunmetal};
                    font: 22px ${Theme.fonts.title};
                    font-weight: bold;
                }
                .contactform input {
                    float: left;
                    width: 40%;
                    padding: 10px 5%;
                    margin: 5px 5%;
                    border-radius: 8px;
                    border: none;
                    font: 14px 'Open Sans';
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .contactform label {
                    float: left;
                    width: 90%;
                    padding: 5px 0%;
                    font: 14px ${Theme.fonts.fancy};
                    margin: 5px 0%;
                }
                .contactform textarea {
                    float: left;
                    width: 80%;
                    padding: 10px 5%;
                    margin: 5px 5%;
                    border-radius: 8px;
                    font: 14px 'Open Sans';
                    border: none;
                    min-height: 140px;
                    resize: vertical;
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .contactform {
                    float: left;
                    width: 100%;
                    opacity: ${loading == true || formStatus == 'succeeded' ? '0': '1'};
                    transition: all .3s ease;
                }
                .column {
                    float: left;
                    width: 48%;
                    margin: 10px 1%;
                    padding: 30px 3%;
                    border-radius: 8px;
                    position: relative;
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .content {
                    float: left;
                    width: 100%;
                    display: flex;
                    justify-content: center;
                }
                .body {
                    float: left;
                    width: 100%;
                    margin-top: 80px;
                }
            `}</style>
        </div>
    )
}

export default Contact
