import Theme from '../styles/theme';
import React from 'react';

export const Span = ({padding, children, background}) => {

    return (
        <div className='span'>
            {children}
            <style jsx>{`
                .span {
                    float: left;
                    width: 100%;
                    padding: ${padding}px 0px;
                    background: ${background};
                }
            `}</style>
        </div>
    )
}

export const Row = ({padding, children, background, margin}) => {

    return (
        <div className='row'>

            <style jsx>{`
                .row {
                    float: left;
                    width: 100%;
                    padding: ${padding};
                    margin: ${margin};
                    background: ${background};
                    min-height: 200px;
                }
            `}</style>
        </div>
    )
}
