import Layout from '../components/layout';
import build from '../components/pagebuild.json';
import BannerSlider from '../components/banner';
import Theme from '../styles/theme';
import { loginRequest } from '../services/apiservice'
import { useState } from 'react'
import { useWindowSize } from '../components/hooks';

function Login() {

    const [inputs, setInputs] = useState({
        username: '',
        password: '',
    })
    const wsize = useWindowSize();
    const [isloading, setLoading] = useState(false);
    const [formStatus, setFormStatus] = useState(false);

    const userLoginRequest = async(e) => {
        e.preventDefault();
        setLoading(true);
        const newLogin = await loginRequest(inputs.username, inputs.password);
        if(newLogin) {
            setFormStatus(newLogin);
            setLoading(false);
        }
    }

    const isTyping = (e) => {
        e.persist();
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
            <div className='body'>
                <img className='bgimage' src='/bg_login.png' />
                <h1 className='header'>BurkeDeveloping</h1>
                <div className='formbody'>
                    <form onSubmit={userLoginRequest}>
                        <label title='username'>Username</label>
                        <input 
                            name='username'
                            value={inputs.username}
                            onChange={isTyping}
                            required 
                        />
                        <label>Password</label>
                        <input 
                            name='password'
                            type='password'
                            value={inputs.password}
                            onChange={isTyping}
                            required 
                        />
                        <div className='formstatus'>{formStatus ? formStatus: null}</div>
                        <button>Login</button>
                    </form>
                </div>
                <style jsx>{`
                    .formbody {
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        width: 400px;
                        height: 500px;
                        backdrop-filter: blur(8px);
                        background: rgba(255, 255, 255, .7);
                        box-shadow: 0 0 4px rgba(25, 26, 24, .2);
                        border-radius: 4px;
                    }
                    .formbody label {
                        float: left;
                        width: 70%;
                        padding: 10px 5%;
                        margin: 2px 15%;
                        font: 14px ${Theme.fonts.subheader};
                    }
                    .formbody input {
                        float: left;
                        width: 60%;
                        padding: 10px 5%;
                        margin: 2px 15%;
                        font: 14px ${Theme.fonts.paragraph};
                    }
                    .formbody button {
                        float: left;
                        width: 60%;
                        padding: 10px 5%;
                        margin: 2px 20%;
                        margin-top: 45px;
                        font: 14px ${Theme.fonts.subheader};
                        border: none;
                        background: ${Theme.colors.platinum};
                    }
                    .formstatus {
                        float: left;
                        width: 90%;
                        padding: 5px 5%;
                    }
                    .formbody form {
                        float: left;
                        width: 100%;
                        margin-top: 70px;
                    }
                    .bgimage {
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        width: 100%;
                        height: 100%;

                    }
                    .header {
                        position: absolute;
                        top: 10%;
                        left: 50%;
                        transform: translateX(-50%);
                        width: auto;
                        height: auto;
                    }
                    .body {
                        float: left;
                        width: 100%;
                        min-height: ${wsize.height}px;
                        height: 100%;
                        position: relative;
                    }
                `}</style>
            </div>
    )
}

export default Login
