import BannerSlider from '../components/banner'
import Theme from '../styles/theme'
import nextCookie from 'next-cookies'
import { createTask, getTasks, removeTask, updateTaskStatus } from '../services/apiservice'
import { createProject } from '../services/projectservice'
import { useState, useEffect } from 'react'
import Router from 'next/router'

const sortingOrder = {
    'inprogress': 1,
    'created': 2,
    'done': 3
}

function compare(key, order = 'asc') {
    return function (a, b) {
        if (!a.hasOwnProperty(key) || !b.hasOwnProperty(key)) 
	    return 0;

	const first = (a[key].toLowerCase() in sortingOrder) ? sortingOrder[a[key]] : Number.MAX_SAFE_INTEGER;
	const second = (b[key].toLowerCase() in sortingOrder) ? sortingOrder[b[key]] : Number.MAX_SAFE_INTEGER;

	let result = 0;
	if (first < second) 
	    result = -1;
	else if (first > second) 
	    result = 1;
	return (order === 'desc') ? ~result : result
    };
}

function Backend({getFirstLoad}) {

    const [inputs, setInputs] = useState({
        objective: '',
        status: 'created',
    })
    const [allTasks, setAllTasks] = useState(getFirstLoad != null ? getFirstLoad: null)
    const [status, setStatus] = useState(getFirstLoad != null ? 'succeeded': 'none')
    let lastcat = null

    // Create a new Task DEFAULT: status: 'created'
    const createNewTask = async(e) => {
        e.preventDefault();
        const newTask = await createTask(inputs.objective, inputs.status)
        if(newTask) {
            console.log(newTask)
            setInputs({...inputs, objective: ''})
            updateTasks(e)
        }
    }

    // Updating Tasks with Backend
    const updateTasks = async(e) => {
        setStatus('loading')
        const getalltasks = await getTasks(50, null)
        if(getalltasks) {
            console.log(getalltasks)
            getalltasks.sort(compare('status'))
            setAllTasks(getalltasks)
            setStatus('succeeded')
        }
    }

    const removeThisTask = async(e, task_id) => {
        e.preventDefault()
        const removenewtask = await removeTask(task_id);
        if(removenewtask) {
            console.log(removenewtask)
            updateTasks(e)
        }
    }

    const updateThisTask = async(e, task_id, status) => {
        e.preventDefault()
        const updatenewtask = await updateTaskStatus(task_id, status);
        if(updatenewtask) {
            console.log(updatenewtask)
            updateTasks(e)
        }
    }

    const isTyping = (e) => {
        e.persist();
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className='body'>
            <img id='body_bg' src='/bg_login.png'/>
            <div id="abovelayer">

            </div>
            <style jsx>{`
                .body {
                    float: left; 
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                    overflow: hidden;
                }
                .cat {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    color: white;
                    font: 12px 'Roboto';
                    background: ${Theme.colors.coral};
                }
                .update {
                    float: left;
                    width: 130px;
                    text-align: center;
                    font: 12px 'Open Sans';
                    padding: 6px;
                    opacity: .9;
                    color: white;
                    border-radius: 8px;
                    background: rgba(70,70,70,.6);
                    transition: opacity .4s ease;
                    cursor: pointer;
                }
                .update:hover {
                    opacity: 1;
                }
                .remove {
                    float: left;
                    width: 75px;
                    text-align: center;
                    font: 12px 'Open Sans';
                    margin-left: 10px;
                    padding: 6px;
                    opacity: .9;
                    color: white;
                    border-radius: 8px;
                    background: rgba(226, 70, 70,.8);
                    cursor: pointer;
                }
                .remove:hover {
                    opacity: 1;
                }
                .threedots {
                    position: absolute;
                    top: -5px;
                    right: -5px;
                    width: 20px;
                    height: 20px;
                    padding: 15px;
                    cursor: pointer;
                }
                .threedots:hover ~ .floater, .floater:hover {
                    opacity: 1;
                    transform: translateX(100%) scale(1,1);
                }
                .floater {
                    position: absolute;
                    right: -2px;
                    top: 10px;
                    transform: translateX(0) scale(0,0);
                    padding: 4px;
                    opacity: 0;
                    overflow: hidden;
                    transition: opacity .2s ease, transform .4s;
                    border-radius: 10px;
                    backdrop-filter: blur(8px);
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 1px rgba(20,20,20,.6);
                    z-index: 90;
                }
                .task {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    position: relative;
                    border-top: 1px solid ${Theme.colors.coral};
                }
                .task h2 {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    margin: 0;
                    font: 14px 'Montserrat';
                }
                .task h3 {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    margin: 0;
                    font: 12px 'Roboto';
                    opacity: .8;
                }
                #abovelayer {
                    float: left;
                    width: 100%;
                    height: 100%;
                    z-index: 99;
                    position: relative;
                    margin-top: 80px;
                }
                #abovelayer textarea {
                    float: left;
                    margin: 10px 2.5%;
                    padding: 10px 2.5%;
                    width: 90%;
                    font: 18px 'Roboto';
                    border: none;
                    resize: vertical;
                    max-height: 150px;
                    min-height: 30px;
                    border-radius: 6px;
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft {
                    float: left;
                    width: 23%;
                    height: 100%;
                    z-index: 99;
                    margin: 10px 1%;
                    border-radius: 10px;
                    backdrop-filter: blur(8px);
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft h1 {
                    float: left;
                    margin: 10px 5%;
                    width: 90%;
                    font: 20px 'Montserrat';
                }
                .sideleft form {
                    float: left;
                    min-height: 200px;
                    width: 90%;
                    border-bottom: 2px solid ${Theme.colors.coral};
                    padding: 10px 5%;
                }
                .sideleft button {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    background: ${Theme.colors.coral};
                    color: white;
                    cursor: pointer;
                    border: none;
                    border-radius: 8px;
                    margin: 10px 5%;
                }
                #body_bg {
                    width: 100%;
                    z-index: -20;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%) scale(1.1,1.1);
                }
            `}</style>
        </div>
    )
}

Backend.getInitialProps = async(ctx) => {
    const cookies = await nextCookie(ctx)
    const getfirsttasks = await getTasks(50, cookies.accessToken)
    if(getfirsttasks == 'Not Authorized') {
        if (ctx.res) {
            ctx.res.writeHead(301, {
              Location: '/login'
            });
            ctx.res.end();
        } 
    }
    getfirsttasks.sort(compare('status'))
    return {getFirstLoad: getfirsttasks}
}

export default Backend
