import BannerSlider from '../components/banner'
import Theme from '../styles/theme'
import nextCookie from 'next-cookies'
import { createTask, getTasks, removeTask, updateTaskStatus } from '../services/apiservice'
import { useState, useEffect } from 'react'

function organizeByStatus(response){
    return response.sort((a, b) => a.status == b.status  ? a.status == 'created' ? -1: 1: 1)
} 

function Backend({getFirstLoad}) {

    const [inputs, setInputs] = useState({
        objective: '',
        status: 'created',
    })
    const [allTasks, setAllTasks] = useState(getFirstLoad != null ? getFirstLoad: null)
    const [status, setStatus] = useState(getFirstLoad != null ? 'succeeded': 'none')
    let lastcat = null

    // Create a new Task DEFAULT: status: 'created'
    const createNewTask = async(e) => {
        e.preventDefault();
        const newTask = await createTask(inputs.objective, inputs.status)
        if(newTask) {
            console.log(newTask)
            setInputs({...inputs, objective: ''})
            updateTasks(e)
        }
    }

    // Updating Tasks with Backend
    const updateTasks = async(e) => {
        setStatus('loading')
        const getalltasks = await getTasks(50, null)
        if(getalltasks) {
            console.log(getalltasks)
            const organize = organizeByStatus(getalltasks)
            setAllTasks(organize)
            setStatus('succeeded')
        }
    }

    const removeThisTask = async(e, task_id) => {
        e.preventDefault()
        const removenewtask = await removeTask(task_id);
        if(removenewtask) {
            console.log(removenewtask)
            updateTasks(e)
        }
    }

    const updateThisTask = async(e, task_id, status) => {
        e.preventDefault()
        const updatenewtask = await updateTaskStatus(task_id, status);
        if(updatenewtask) {
            console.log(updatenewtask)
        }
    }

    const isTyping = (e) => {
        e.persist();
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className='body'>
            <img id='body_bg' src='/bg_login.png'/>
            <div id="abovelayer">
                <div className='sideleft'>
                    <form onSubmit={createNewTask}>
                        <h1>New Task</h1>
                        <textarea 
                            name='objective'
                            onChange={isTyping}
                            value={inputs.objective}
                            placeholder='Objective'
                            required
                        />
                        <button>Add Task</button>
                    </form>
                    <div className='tasklist'>
                        <h1>My Tasks</h1>
                        {allTasks ? allTasks.map((task, i) => {
                            let nextStatus = task.status == 'created' ? 'inprogress': 'done';
                            if(lastcat == task.status) {
                                return (
                                    <div key={i} className='task'>
                                        <h2>Objective: {task.objective}</h2>
                                        <h3>Status: {task.status}</h3>
                                        <img className='threedots' src='/three_dots.png'/>
                                        <div className='floater'>
                                            <div onClick={(e) => updateThisTask(e, task.id, 'inprogress')} className='update'>Update Status</div>
                                            <div onClick={(e) => removeThisTask(e, task.id)} className='remove'>Remove Task</div>
                                        </div>
                                    </div>
                                )
                            } else {
                                lastcat = task.status;
                                return (
                                    <>
                                        <div className='cat'>{task.status.toUpperCase()}</div>
                                        <div key={i} className='task'>
                                            <h2>Objective: {task.objective}</h2>
                                            <h3>Status: {task.status}</h3>
                                            <img className='threedots' src='/three_dots.png'/>
                                            <div className='floater'>
                                                <div onClick={(e) => updateThisTask(e, task.id, 'inprogress')} className='update'>Update Status</div>
                                                <div onClick={(e) => removeThisTask(e, task.id)} className='remove'>Remove Task</div>
                                            </div>
                                        </div>
                                    </>
                                )
                            }
                        }): status == 'none' ? <h2>No Tasks yet</h2>: <div className='loading'></div>}
                    </div>
                </div>
            </div>
            <style jsx>{`
                .body {
                    float: left; 
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                    overflow: hidden;
                }
                .update {
                    float: left;
                    width: 100px;
                    text-align: center;
                    font: 12px 'Open Sans';
                    padding: 6px;
                    opacity: .7;
                    border-radius: 8px;
                    background: rgba(70,70,70,.6);
                    transition: opacity .4s ease;
                    cursor: pointer;
                }
                .update:hover {
                    opacity: 1;
                }
                .remove {
                    float: left;
                    width: 100px;
                    text-align: center;
                    font: 12px 'Open Sans';
                    margin-left: 10px;
                    padding: 6px;
                    opacity: .7;
                    border-radius: 8px;
                    background: rgba(226, 70, 70,.8);
                    cursor: pointer;
                }
                .remove:hover {
                    opacity: 1;
                }
                .threedots {
                    position: absolute;
                    top: -5px;
                    right: -5px;
                    width: 20px;
                    height: 20px;
                    padding: 25px;
                    cursor: pointer;
                }
                .threedots:hover ~ .floater, .floater:hover {
                    opacity: 1;
                    transform: translateX(100%) scale(1,1);
                }
                .floater {
                    position: absolute;
                    right: -2px;
                    top: 10px;
                    transform: translateX(0) scale(0,0);
                    padding: 4px;
                    opacity: 0;
                    overflow: hidden;
                    transition: opacity .2s ease, transform .4s;
                    border-radius: 10px;
                    backdrop-filter: blur(8px);
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 1px rgba(20,20,20,.6);
                    z-index: 90;
                }
                .task {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    position: relative;
                    border-top: 1px solid ${Theme.colors.coral};
                }
                .task h2 {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    margin: 0;
                    font: 14px 'Montserrat';
                }
                .task h3 {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    margin: 0;
                    font: 12px 'Roboto';
                    opacity: .8;
                }
                #abovelayer {
                    float: left;
                    width: 100%;
                    height: 100%;
                    z-index: 99;
                    margin-top: 80px;
                }
                #abovelayer textarea {
                    float: left;
                    margin: 10px 2.5%;
                    padding: 10px 2.5%;
                    width: 90%;
                    font: 18px 'Roboto';
                    border: none;
                    resize: vertical;
                    max-height: 150px;
                    min-height: 30px;
                    border-radius: 6px;
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft {
                    float: left;
                    width: 300px;
                    height: 100%;
                    z-index: 99;
                    margin: 10px;
                    border-radius: 10px;
                    backdrop-filter: blur(8px);
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft h1 {
                    float: left;
                    margin: 10px 5%;
                    width: 90%;
                    font: 20px 'Montserrat';
                }
                .sideleft form {
                    float: left;
                    min-height: 200px;
                    width: 90%;
