import Theme from '../styles/theme';

export function Span({padding, children, background}) {

    return (
        <div className='span'>
            {children}
            <style jsx>{`
                .span {
                    float: left;
                    width: 100%;
                    padding: ${padding}px 0px;
                    background: ${background};
                }
            `}</style>
        </div>
    )
}
export function Row({padding, children, background, margin}) {

    return (
        <div className='row'>
            {children}
            <style jsx>{`
                .row {
                    float: left;
                    width: 100%;
                    padding: ${padding};
                    margin: ${margin};
                    background: ${background};
                    min-height: 200px;
                }
            `}</style>
        </div>
    )
}
