import Theme from '../styles/theme';
import React from 'react';

export const Span = ({padding, children, background}) => {

    return (
        <div className='span'>
            {children}
            <style jsx>{`
                .span {
                    float: left;
                    width: 100%;
                    padding: ${padding}px 0px;
                    background: ${background};
                }
            `}</style>
        </div>
    )
}

export class Row extends React.Component {
    render () {
    const colcount = React.Children.count(this.props.children)
    const newchildren = React.Children.map(this.props.children, child => {
        return React.cloneElement(child, {width: 100 / colcount})
    })
    return (
        <div className='row'>
            {newchildren}
            <style jsx>{`
                .row {
                    float: left;
                    width: 100%;
                    padding: ${padding};
                    margin: ${margin};
                    background: ${background};
                    min-height: 200px;
                }
            `}</style>
        </div>
    )        
}
}

export const Col = ({padding, children, background, margin, width}) => {

    return (
        <div className='col'>
            {children}
            <style jsx>{`
                .col {
                    float: left;
                    width: ${width}%;
                    padding: ${padding};
                    margin: ${margin};
                    background: ${background};
                    min-height: 200px;
                }
            `}</style>
        </div>
    )
}
