import { useState } from 'react'
import { baseConfig } from '../../services/restservice'

function UploadImage({firstLoadImages}) {

    const [images, setImages] = useState(firstLoadImages)
    const [file, setFile] = useState(null)
    const [preview, setPreview] = useState(null)

    const uploadImageSingle = async(e) => {
        e.preventDefault()
        if(file == null) return 
        const formData = new FormData()
        formData.append("myFile", file)
        const uploadImage = await fetch(baseConfig.baseURL + '/uploadfile', {
            method: 'POST',
            body: formData
        })
        .then(response => {
            return response.json()
        })
        if(uploadImage) {
            setPreview(null)
            setFile(null)
            loadImages(e)
        }
    }

    const uploadImageMultiple = async(e) => {
        e.preventDefault()
        console.log(file)
        if(file == null) return 
        const formData = new FormData()

        for (let i = 0; i < file.length; i++) {
            console.log(file[i].uri)
            formData.append(`myImages[]`, {
                uri: file[i].uri,
                type: 'image/*',
                name: file[i].name
            })
        }

        console.log(formData)
        const newupload = await fetch('http://localhost:3600/uploadmultiple', {
            method: 'POST',
            body: formData
        })
        .then(response => {
            return response.json()
        })
        console.log(newupload)
        if(newupload) {
            setPreview(null)
            setFile(null)
            console.log(newupload)
            loadImages(e)
        }
    }

    const handleFileChangeSingle = (e) => {
        e.persist()
        setPreview(URL.createObjectURL(e.target.files[0]))
        setFile(e.target.files[0])
    }

    const removeImage = async(e, image) => {
        e.preventDefault()
        const newremove = await fetch(baseConfig.baseURL + '/removeimage/' + image, {
            method: 'DELETE',
        }).then(response => {
            return response.status
        })
        if(newremove) {
            console.log(newremove)

        }
    }

    const loadImages = async(e) => {
        e.preventDefault()
        const getImages = await fetch('http://localhost:3600/getallimages', {
            method: 'GET',
        }).then (response => {
            return response.json()
        })
        if(getImages) {
            setImages(getImages.images)
        }
    }

    return (
        <div className='body'>
            <div className='uploadform'>
                <form onSubmit={uploadImageSingle}>
                    <div className='previewframe'><img className='preview' src={preview ? preview: '/imageplaceholder.png'} /></div>
                    <label htmlFor='fileid'>{file ? file.name: 'Select image'}</label>
                    <input id='fileid' type='file' accept='image/*' onChange={handleFileChangeSingle} required />
                    <button type='submit'>Upload Image</button>
                </form>
            </div>
            <div className='images'>
                    <h1>My Images</h1>
                    <div className='imageholder'>
                        {images != null ? images.map((image, i) => {
                            return (
                                <div key={i} className='imgframe'>
                                    <div onClick={(e) => removeImage(e, image)} className='x'>x</div>
                                    <img src={baseConfig.backendImages + image} />
                                </div>
                            )
                        }): <h2 onClick={loadImages}>Load Images</h2>}
                    </div>
                </div>
            <style jsx>{`
                .imageholder {
                    float: left;
                    width: 100%;
                    margin-top: 10px;
                    display: flex;
                    flex-wrap: wrap;
                    justify-content: center;
                }
                .imgframe {
                    float: left;
                    width: 200px;
                    height: 200px;
                    margin: 8px;
                    position: relative;
                }
                .imgframe img {
                    position: absolute;
                    width: 100%;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                }
                .images {
                    float: left;
                    width: 60%;
                }
                .images h1 {
                    float: left;
                    font: 36px 'Montserrat';
                    margin: 0;
                    padding: 10px;
                }
                .uploadform input {
                    display: none;
                }
                .uploadform label {
                    position: absolute;
                    top: 60px;
                    left: 20px;
                    padding: 10px;
                    cursor: pointer;
                    opacity: .7;
                    transition: all .3s ease;
                    font: 14px 'Roboto';
                }
                .uploadform label:hover {
                    opacity: 1;
                }
                .uploadform button {
                    position: absolute;
                    bottom: 90px;
                    left: 20px;
                    padding: 10px;
                    cursor: pointer;
                    opacity: .7;
                    transition: all .3s ease;
                    font: 14px 'Roboto';
                }
                .uploadform .previewframe {
                    position: absolute;
                    right: 20px;
                    top: 20px;
                    width: 300px;
                    max-height: 300px;
                    overflow: hidden;
                    display: flex;
                    justify-content: center;
                    align-content: center;
                }
                .uploadform .previewframe img {
                    float: left;
                    width: 100%;
                    height: auto;
                }
                .uploadform form {
                    float: left;
                    width: 100%;
                    height: 100%;
                }
                .uploadform {
                    float: left;
                    width: 40%;
                    position: relative;
                    min-height: 300px;
                }
                .body {
                    float: left;
                    width: 100%;
                    margin-top: 80px;
                }
            `}</style>
        </div>
    )
}

UploadImage.getInitialProps = async(e) => {

    const getimg = await fetch(baseConfig.baseURL + '/getallimages', {
        method: 'GET',
    }).then (response => {
        return response.json()
    })
    return {firstLoadImages: getimg.images}
}

export default UploadImage
