import Theme from '../styles/theme';
import { useState } from 'react';

function Contact() {

    const [inputs, setInputs] = useState({
        fullname: '',
        email: '',
        request: ''
    })

    const handleType = (e) => {
        e.persist()
        setInputs({...inputs, [e.target.name]: e.target.value})
    }

    const handleContact = async(e) => {
        e.preventDefault()
    }

    return (
        <div className='body'>
            <div className='content'>
                <form className='contactform' onSubmit={handleContact}>
                    <label>First Name</label>
                    <input 
                        onChange={handleType}
                        name='firstname'
                        value={inputs.firstname}
                        required
                    />
                    <label>Email</label>
                    <input 
                        onChange={handleType}
                        name='email'
                        value={inputs.email}
                        required
                    />
                    <label>What can we help you with</label>
                    <textarea 
                        onChange={handleType}
                        name='request'
                        value={inputs.request}
                        required
                    />
                </form>
            </div>
            <style jsx>{`
                .contactform input {
                    float: left;
                    width: 80%;
                    padding: 10px 5%;
                    margin: 5px 5%;
                    border-radius: 8px;
                    border: none;
                    font: 14px 'Open Sans';
                    box-shadow: ${Theme.colors.shadow};
                }
                .contactform label {
                    float: left;
                    width: 80%;
                    padding: 5px 5%;
                    font: 14px 'Roboto';
                    margin: 5px 5%;
                }
                .contactform textarea {
                    float: left;
                    width: 80%;
                    padding: 5px 5%;
                    margin: 5px 5%;
                    border-radius: 8px;
                    font: 14px 'Open Sans';
                    border: none;
                    resize: vertical;
                    box-shadow: ${Theme.colors.shadow};
                }
                .contactform {
                    float: left;
                    width: 48%;
                    margin: 10px 1%;
                    padding: 10px 1%;
                    display: flex;
                    justify-content: center;
                    flex-wrap: wrap;
                }
                .content {
                    float: left;
                    width: 100%;
                    display: flex;
                    justify-content: center;
                }
                .body {
                    float: left;
                    width: 100%;
                    margin-top: 80px;
                }
            `}</style>
        </div>
    )
}

export default Contact
