import Theme from '../styles/theme';
import { useState } from 'react';

function Contact() {

    const [inputs, setInputs] = useState({
        fullname: '',
        email: '',
        request: ''
    })

    const handleType = (e) => {
        e.persist()
        setInputs({...inputs, [e.target.name]: e.target.value})
    }

    const handleContact = async(e) => {
        e.preventDefault()
    }

    return (
        <div className='body'>
            <div className='content'>
                <form className='contactform' onSubmit={handleContact}>
                    <img className='banner' src='/burke_wide_title_black.png' />
                    <label>First Name</label>
                    <input 
                        onChange={handleType}
                        name='firstname'
                        value={inputs.firstname}
                        required
                    />
                    <label>Email</label>
                    <input 
                        onChange={handleType}
                        name='email'
                        value={inputs.email}
                        required
                    />
                    <label>What can we help you with</label>
                    <textarea 
                        onChange={handleType}
                        name='request'
                        value={inputs.request}
                        required
                    />
                </form>
                <div className='cinfo'>
                    <h1>Burke Developing</h1>
                    <ul>
                        <li><img src='/icons/icon_boss.png'/>Daniel Burke</li>
                        <li><img src='/icons/icon_phone.png'/>1-781-426-1894</li>
                        <li><img src='/icons/icon_email.png'/>dan@burkedeveloping.com</li>
                    </ul>
                </div>
            </div>
            <style jsx>{`
                .cinfo ul {
                    float: left;
                    width: 90%;
                    background: ${Theme.colors.lightplatinum};
                    padding: 20px 5%;
                }
                .cinfo ul li {
                    float: left;
                    width: 100%;
                    margin: 20px 0;
                    list-style: none;
                    font: 14px ${Theme.fonts.subheader};
                }
                .cinfo ul li img {
                    width: 20px;
                    height: 20px;
                    margin-right: 10px;
                }
                .cinfo {
                    width: 35%;
                    float: left;
                    min-height: 400px;
                    margin: 10px 1%;
                    padding: 10px 1%;
                    border-radius: 8px;
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .cinfo h1 {
                    float: left;
                    width: 95%;
                    padding: 10px 2.5%;
                    margin: 0;
                    font: 18px 'Montserrat';
                }
                .banner {
                    float: left;
                    width: 80%;
                    margin: -40px 10%;

                }
                .contactform input {
                    float: left;
                    width: 40%;
                    padding: 10px 5%;
                    margin: 5px 5%;
                    border-radius: 8px;
                    border: none;
                    font: 14px 'Open Sans';
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .contactform label {
                    float: left;
                    width: 90%;
                    padding: 5px 0%;
                    font: 14px ${Theme.fonts.fancy};
                    margin: 5px 0%;
                }
                .contactform textarea {
                    float: left;
                    width: 80%;
                    padding: 5px 5%;
                    margin: 5px 5%;
                    border-radius: 8px;
                    font: 14px 'Open Sans';
                    border: none;
                    min-height: 140px;
                    resize: vertical;
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .contactform {
                    float: left;
                    width: 48%;
                    margin: 10px 1%;
                    padding: 30px 3%;
                    border-radius: 8px;
                    box-shadow: ${Theme.colors.shadowlight};
                }
                .content {
                    float: left;
                    width: 100%;
                    display: flex;
                    justify-content: center;
                }
                .body {
                    float: left;
                    width: 100%;
                    margin-top: 80px;
                }
            `}</style>
        </div>
    )
}

export default Contact
