import BannerSlider from '../components/banner'
import Theme from '../styles/theme'
import nextCookie from 'next-cookies'
import { createTask, getTasks, removeTask } from '../services/apiservice'
import { useState, useEffect } from 'react'

function Backend({getFirstLoad}) {

    const [inputs, setInputs] = useState({
        objective: '',
        status: 'created',
    })
    const [allTasks, setAllTasks] = useState(getFirstLoad != null ? getFirstLoad: null)
    const [status, setStatus] = useState(getFirstLoad != null ? 'succeeded': 'none')

    // Create a new Task DEFAULT: status: 'created'
    const createNewTask = async(e) => {
        e.preventDefault();
        const newTask = await createTask(inputs.objective, inputs.status)
        if(newTask) {
            console.log(newTask)
            updateTasks(e)
        }
    }

    // Updating Tasks with Backend
    const updateTasks = async(e) => {
        setStatus('loading')
        const getalltasks = await getTasks(50, null)
        if(getalltasks) {
            console.log(getalltasks)
            setAllTasks(getalltasks)
            setStatus('succeeded')
        }
    }

    const removeThisTask = async(e, task_id) => {
        e.preventDefault()
        const removenewtask = await removeTask(task_id);
        if(removenewtask) {
            console.log(removenewtask)
            updateTasks(e)
        }
    }

    const isTyping = (e) => {
        e.persist();
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className='body'>
            <img id='body_bg' src='/bg_login.png'/>
            <div id="abovelayer">
                <div className='sideleft'>
                    <form onSubmit={createNewTask}>
                        <h1>New Task</h1>
                        <textarea 
                            name='objective'
                            onChange={isTyping}
                            value={inputs.objective}
                            placeholder='Objective'
                            required
                        />
                        <button>Add Task</button>
                    </form>
                    <div className='tasklist'>
                        <h1>My Tasks</h1>
                        {allTasks ? allTasks.map((task, i) => {
                            let edit = 'false'
                            const toggleEdit = (e) => {
                                e.persist();
                                if(edit == 'false') edit = 'true';
                                else edit = 'false'
                            }
                            return (
                                <div key={i} className='task'>
                                    <h2>Objective: {task.objective}</h2>
                                    <h3>Status: {task.status}</h3>
                                    <img onClick={toggleEdit} className='threedots' src='/three_dots.png'/>
                                    <div className='floater' title={edit}>
                                        <div className='update'>Update Status</div>
                                        <div className='remove'>Remove Task</div>
                                    </div>
                                </div>
                            )
                        }): status == 'none' ? <h2>No Tasks yet</h2>: <div className='loading'></div>}
                    </div>
                </div>
            </div>
            <style jsx>{`
                .body {
                    float: left; 
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                    overflow: hidden;
                }
                .threedots {
                    position: absolute;
                    top: 10px;
                    right: 10px;
                    width: 30px;
                    height: 30px;
                    cursor: pointer;
                }
                .floater {
                    position: absolute;
                    right: -10px;
                    top: 10px;
                    transform: translateX(100%);
                    padding: 10px;
                    width: auto;
                    background: white;
                    opacity: 1;
                    z-index: 90;
                }
                .floater[title="false"] {
                    opacity: 0;
                    z-index: -10;
                }
                .task {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    position: relative;
                    border-top: 1px solid ${Theme.colors.coral};
                }
                .task h2 {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    margin: 0;
                    font: 14px 'Montserrat';
                }
                .task h3 {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    margin: 0;
                    font: 12px 'Roboto';
                    opacity: .8;
                }
                #abovelayer {
                    float: left;
                    width: 100%;
                    height: 100%;
                    z-index: 99;
                    margin-top: 80px;
                }
                .sideleft h1 {
                    float: left;
                    margin: 10px 5%;
                    width: 90%;
                    font: 20px 'Montserrat';
                }
                #abovelayer textarea {
                    float: left;
                    margin: 10px 2.5%;
                    padding: 10px 2.5%;
                    width: 90%;
                    font: 18px 'Roboto';
                    border: none;
                    resize: vertical;
                    max-height: 150px;
                    min-height: 30px;
                    border-radius: 6px;
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft {
                    float: left;
                    width: 300px;
                    height: 100%;
                    z-index: 99;
                    margin: 10px;
                    border-radius: 10px;
                    backdrop-filter: blur(8px);
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft form {
                    float: left;
                    min-height: 200px;
                    width: 90%;
                    border-bottom: 2px solid ${Theme.colors.coral};
                    padding: 10px 5%;
                }
                .sideleft button {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    background: ${Theme.colors.coral};
                    color: white;
                    cursor: pointer;
                    border: none;
                    border-radius: 8px;
                    margin: 10px 5%;
                }
                #body_bg {
                    width: 100%;
                    z-index: -20;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%) scale(1.1,1.1);
                }
            `}</style>
        </div>
    )
}

Backend.getInitialProps = async(ctx) => {
    const cookies = await nextCookie(ctx)
    const getfirsttasks = await getTasks(50, cookies.accessToken)
    console.log(getfirsttasks)
    return {getFirstLoad: getfirsttasks}
}

export default Backend
