import React from 'react'
import App from 'next/app'
import Layout from '../components/layout'
import build from '../components/pagebuild.json';
import Router from 'next/router'


class MyApp extends App {
  constructor(props) {
    super(props);
    this.state = {
      loaded: false,
    };
  }

  componentDidMount() {
    this.setState({ loaded: true });
    Router.events.on('routeChangeStart', () => this.setState({ loaded: false }));
    Router.events.on('routeChangeComplete', () => this.setState({ loaded: true }));
  }

  render() {
    const { Component, pageProps, router } = this.props

    const { loaded } = this.state

    if(loaded) {
      return (
       <Layout links={build.paths} title={build.title} path={router.pathname} >
         <Component {...pageProps}></Component>
       </Layout>
     )
    }
    return (
      <LoadingScreen loader='/loading.gif' />
    )
  }
}

export default MyApp
