import Theme from '../styles/theme'
import nextCookie from 'next-cookies'
import {  } from '../services/apiservice'
import { useState } from 'react'
import { createProject } from '../services/projectservice'
import TaskManager, {compare} from '../components/backend/taskmanager'

function Backend({getFirstLoad}) {

    const [file, setFile] = useState(null)

    const uploadImage = async(e) => {

    }

    const handleFileChange = (e) => {
        setFile(URL.createObjectURL(e.target.files[0]))
    }

    return (
        <div className='body'>
            <img id='body_bg' src='/bg_login.png'/>
            <div id="abovelayer">
                <TaskManager getFirstLoad={getFirstLoad} />
                <div className='imageupload'>
                    <form>
                        <img src={file ? file: '/noimage.png'} />
                        <label htmlFor='fileid'>Upload Image</label>
                        <input id='fileid' type='file' onChange={handleFileChange} />
                        <button type='submit'>Upload File</button>
                    </form>
                </div>
            </div>
            <style jsx>{`
                .imageupload {
                    float: left;
                    width: 23%;
                    margin: 20px 1%;
                    position: relative;
                    border-radius: 8px;
                    overflow: hidden;
                }
                .imageupload form {
                    width: 100%;
                    float: left;
                }
                .imageupload label {
                    float: left;
                    width: 80%;
                    cursor: pointer;
                    padding: 14px 0;
                    margin: 10px 10%;
                    border-radius: 8px;
                    background: ${Theme.colors.onxy};
                    color: white;
                    text-align: center;
                    font: 14px 'Roboto';
                }
                .imageupload button {
                    float: left;
                    width: 80%;
                    border: none;
                    border-radius: 8px;
                    cursor: pointer;
                    padding: 14px 0;
                    margin: 10px 10%;
                    background: ${Theme.colors.onxy};
                    color: white;
                    text-align: center;
                    font: 14px 'Roboto';
                }
                .imageupload img {
                    width: 100%;
                    float: left;
                }
                #fileid {
                    display: none;
                }
                .body {
                    float: left; 
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                    overflow: hidden;
                }
                #abovelayer {
                    float: left;
                    width: 100%;
                    height: 100%;
                    z-index: 99;
                    position: relative;
                    margin-top: 80px;
                }
                #body_bg {
                    width: 100%;
                    z-index: -20;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%) scale(1.1,1.1);
                }
            `}</style>
        </div>
    )
}

Backend.getInitialProps = async(ctx) => {
    const cookies = await nextCookie(ctx)
    const getfirsttasks = await getTasks(50, cookies.accessToken)
    if(getfirsttasks == 'Not Authorized') {
        if (ctx.res) {
            ctx.res.writeHead(301, {
              Location: '/login'
            });
            ctx.res.end();
        } 
    }
    getfirsttasks.sort(compare('status'))
    return {getFirstLoad: getfirsttasks}
}

export default Backend
