import Layout from '../components/layout';
import build from '../components/pagebuild.json';
import BannerSlider from '../components/banner';
import Theme from '../styles/theme';
import { getProjects } from '../services/projectservice';
import Link from 'next/link';
import { baseConfig } from '../services/restservice'
function Portfolio({projects}) {
    return (
        <div className='body'>
            <h1 id='title'>Home Improvement Projects</h1>
            <div id='projectcontainer'>
                {projects == null ? null: projects.map((project, i) => {
                    return (
                        <div className='project' key={i}>
                            <h2>{project.title}</h2>
                            <div className='frame'>
                                <img src={baseConfig.backendImages + project.images[0]} />
                            </div>
                            <p>{project.body.split('').map((char, i) => {
                                if(i <= 200) {
                                    return char
                                } 
                                if(i == project.body.length - 1) {
                                    return ' ...'
                                }
                            })}</p>
                            <Link href={`/posts/${project.id}`}><h4>View Post</h4></Link>
                        </div>
                    )
                })}
            </div>
            <style jsx>{`
                .project {
                    float: left;
                    width: 300px;
                    height: 400px;
                    padding: 10px;
                    margin: 0 10px;
                    border-bottom: 1px solid ${Theme.colors.onxy};
                }
                .frame {
                    float: left;
                    display: flex;
                    justify-content: center;
                    width: 100%;
                    max-height: 300px;
                }
                .project h2 {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    font: 14px 'Montserrat';
                    color: ${Theme.colors.onxy};
                }
                .project p {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    font: 12px 'Open Sans';
                    color: ${Theme.colors.onxy};
                }
                #projectcontainer {
                    float: left;
                    width: 95%;
                    min-height: 600px;
                    margin: 20px 2.5%;
                    display: flex;
                    flex-wrap: wrap;
                    justify-content: center;
                }
                .body {
                    float: left;
                    width: 100%;
                    height: 100%;
                    margin-top: 80px;
                }
                #title {
                    float: left;
                    width: 100%;
                    font: 20px 'Montserrat';
                    margin: 20px;
                    text-align: center;
                }
            `}</style>
        </div>
    )
}

Portfolio.getInitialProps = async(ctx) => {
    const getAllProjects = await getProjects(50);
    return {projects: getAllProjects}
}

export default Portfolio
