import build from '../components/pagebuild.json';
import BannerSlider from '../components/banner';
import Theme from '../styles/theme';
import { loginRequest } from '../services/apiservice'
import { useState } from 'react'

function Login() {

    const [inputs, setInputs] = useState({
        username: '',
        password: '',
    })
    const [isloading, setLoading] = useState(false);

    const userLoginRequest = async(e) => {
        e.preventDefault();
        setLoading(true);
        const newLogin = await loginRequest(inputs.username, inputs.password);
        if(newLogin) {
            setFormStatus(newLogin);
            setLoading(false);
        }
    }

    return (
            <div className='body'>
                <div className='formbody'>
                    <form onSubmit={userLoginRequest}>
                        <label title='username'>Username</label>
                        <input 
                            name='username'
                            value={inputs.username}
                            onChange={isTyping}
                            required 
                        />
                        <label>Password</label>
                        <input 
                            name='password'
                            type='password'
                            value={inputs.password}
                            onChange={isTyping}
                            required 
                        />
                        <div className='formstatus'>{formStatus ? formStatus: null}</div>
                        <button>Login</button>
                    </form>
                </div>
                <style jsx>{`
                    .formbody {
                        position: absolute;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%);
                        width: 400px;
                        height: 600px;

                    }
                    .body {
                        float: left;
                        width: 100%;
                        height: 100%;
                    }
                `}</style>
            </div>
    )
}

export default Login
