import BannerSlider from '../components/banner'
import Theme from '../styles/theme'
import nextCookie from 'next-cookies'
import { createTask } from '../services/apiservice'
import { useState, useEffect } from 'react'

function Backend() {

    const [inputs, setInputs] = useState({
        username: '',
        password: '',
    })

    const userLoginRequest = async(e) => {
        e.preventDefault();
        setFormStatus('loading');
        const newLogin = await createTask(inputs.objective, inputs.status);
        if(newLogin) {
            setFormStatus('failed');
            setStatus(newLogin)
        }
    }

    const isTyping = (e) => {
        e.persist();
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className='body'>

            <img id='body_bg' src='/bg_login.png'/>
            <style jsx>{`
                .body {
                    float: left; 
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                    overflow: hidden;
                }
                #body_bg {
                    width: 100%;
                    z-index: 0;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%) scale(1.1,1.1);
                }
            `}</style>
        </div>
    )
}

Backend.getInitialProps = async(ctx) => {
    const cookies = nextCookie(ctx)
    console.log(cookies);
    return {status: "Authorized"}
}

export default Backend
