import BannerSlider from '../components/banner'
import Theme from '../styles/theme'
import nextCookie from 'next-cookies'
import { createTask, getTasks } from '../services/apiservice'
import { useState, useEffect } from 'react'

function Backend() {

    const [inputs, setInputs] = useState({
        objective: '',
        status: 'created',
    })
    const [allTasks, setAllTasks] = useState(null)
    const [status, setStatus] = useState('onpage')

    // Create a new Task DEFAULT: status: 'created'
    const createNewTask = async(e) => {
        e.preventDefault();
        const newTask = await createTask(inputs.objective, inputs.status)
        console.log(newTask)
    }

    // Updating Tasks with Backend
    useEffect(() => {
        async function updateTasks() {
            const getalltasks = new getTasks(50, null)
            if(getalltasks) {
                console.log(getalltasks)
                setAllTasks(getalltasks)
                setStatus('succeeded')
            }
        }
        setStatus('loading')
        updateTasks()
    }, [allTasks == null && status != 'loading'])

    const isTyping = (e) => {
        e.persist();
        setInputs({
            ...inputs,
            [e.target.name]: e.target.value
        })
    }

    return (
        <div className='body'>
            <img id='body_bg' src='/bg_login.png'/>
            <div id="abovelayer">
                <div className='sideleft'>
                    <form onSubmit={createNewTask}>
                        <h1>New Task</h1>
                        <input 
                            name='objective'
                            onChange={isTyping}
                            value={inputs.objective}
                            placeholder='Objective'
                            required
                        />
                    </form>
                </div>
            </div>
            <style jsx>{`
                .body {
                    float: left; 
                    width: 100%;
                    height: 100%;
                    z-index: 1;
                    overflow: hidden;
                }
                #abovelayer {
                    float: left;
                    width: 100%;
                    height: 100%;
                    z-index: 99;
                    margin-top: 80px;
                }
                #abovelayer h1 {
                    float: left;
                    margin: 10px 5%;
                    width: 90%;
                    font: 20px 'Montserrat';
                }
                #abovelayer input {
                    float: left;
                    margin: 10px 2.5%;
                    padding: 10px 2.5%;
                    width: 90%;
                    font: 18px 'Roboto';
                    border: none;
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft {
                    float: left;
                    width: 300px;
                    height: 100%;
                    z-index: 99;
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                }
                .sideleft form {
                    float: left;
                    min-height: 200px;
                    width: 100%;
                    border-bottom: 2px solid ${Theme.colors.coral};
                    padding: 10px;
                    border-radius: 8px;
                }
                .sideleft form {
                    float: left;
                    min-height: 200px;
                    width: 300px;
                    z-index: 99;
                    background: rgba(255, 255, 255, .7);
                    box-shadow: 0 0 2px rgba(20,20,20,.8);
                    margin: 10px;
                    padding: 10px;
                    border-radius: 8px;
                }
                #body_bg {
                    width: 100%;
                    z-index: -20;
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%) scale(1.1,1.1);
                }
            `}</style>
        </div>
    )
}

Backend.getInitialProps = async(ctx) => {
    const cookies = nextCookie(ctx)
    console.log(cookies);
    return {status: "Authorized"}
}

export default Backend
