import { getProjectById } from "../../services/projectservice"
import BannerSlider from '../../components/banner'
import { baseConfig } from "../../services/restservice"
import Theme from '../../styles/theme'
import { Row, Col } from '../../components/elements'

function Project({project}) {
    return (
        <div id='body'>
            <BannerSlider height={600} images={project.images} backendImageRoute={baseConfig.backendImages}>
                <h1>{project.title}</h1>
            </BannerSlider>
            <div className='tagholder'>
                {project.tags.map((tag, i) => {
                    return <h3 key={i}>{tag.toUpperCase() + (i == project.tags.length - 1 ? '': ',')}</h3>
                })}
            </div>
            <div className='title'>{project.title}</div>
            <Row height='600px'>
                <Col><p>{project.body}</p></Col>
                <Col></Col>
            </Row>
            <style jsx>{`
                .title {
                    float: left;
                    width: 90%;
                    padding: 10px 5%;
                    margin: 0;
                    font: 32px 'Montserrat';
                }
                h1 {
                    float: left;
                    width: auto;
                    margin: 50px 20px;
                    margin-top: 105px;
                    padding: 18px;
                    background: rgba(255,255,255,.5);
                    backdrop-filter: blur(8px);
                    border-radius: 8px;
                    color: ${Theme.colors.onxy};
                    font: 40px ${Theme.fonts.title};
                }
                .tagholder {
                    float: left;
                    width: 95%;
                    padding: 40px 2.5%;
                    border-bottom: 1px solid ${Theme.colors.charcoal};
                    display: flex;
                    justify-content: center;
                }
                h3 {
                    float: left;
                    margin: 4px;
                    font: 16px 'Open Sans';
                    font-style: italic;
                    cursor: pointer;
                    opacity: .8;
                    transition: all .3s ease;
                    color: ${Theme.colors.charcoal};
                }
                h3:hover {
                    opacity: 1;
                    text-decoration: underline;
                }
                p {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%,-50%);
                    width: 80%;
                    padding: 40px 5%;
                    max-height: 420px;
                    overflow-y: scroll;
                    font: 14px 'Open Sans';
                }
                #body {
                    float: left;
                    width: 100%;
                    margin-top: 80px;
                }
            `}</style>
        </div>
    )
}

Project.getInitialProps = async(ctx) => {
    const getNewProject = await getProjectById(ctx.query.id)
    return {project: getNewProject}
}

export default Project
