import { useWindowSize, useScroll } from './hooks';
import { useState, useEffect } from 'react';

export function BannerSingle({defaultBackground, image, children, customHeight, fullsize, parallax}) {

    const getWindow = useWindowSize();
    const getScroll = useScroll();
    const defaultSize = 600;


    useEffect(() => {
        console.log('hit');
    }, []);

    return (
        <div id='banner'>
            <img className='singleimage' src={image} style={{top: parallax == true && getScroll.scrollY >=0 ? (((fullsize == true ? getWindow.height: customHeight) / 2) - (getScroll.scrollY / 3)) || 0: '50%'}}/>
            <div className='content'>{children}</div>
            <style jsx>{`
                #banner {
                    float: left;
                    width: 100%;
                    position: relative;
                    overflow: hidden;
                    background: ${defaultBackground};
                    height: ${customHeight ? customHeight: (fullsize == true ? getWindow.height: defaultSize)}px;
                }
                .singleimage {
                    position: absolute;
                    left: 50%;
                    width: 100%;
                    z-index: 10;
                    transition: all .2s ease;
                    transform: translate(-50%, -50%) scale(1.2, 1.2);
                }
                @media only screen and (max-width: 800px) {
                } 
                @media only screen and (max-width: 1100px) {
                }
            `}</style>
        </div>
    )
}

function Slide({index, image, height, currentSlide}) {
    return (
        <div className='slide'>
            <img src={image} />
            <style jsx>{`
                .slide {
                    position: absolute;
                    top: 0;
                    left: ${index * 100}%;
                    width: 100%;
                    height: ${height}px;
                    overflow: hidden;
                    z-index: 10;
                    transform: translate3d(0, 0, 0);
                }
                .slide img {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transition: all 2s ease;
                    transform: translate(-50%,-50%) scale(${index == currentSlide ? '1.2, 1.2': '1, 1'});
                    width: 100%;

                }
            `}</style>
        </div>
    )
}

export default function BannerSlider({cover, defaultBackground, images, children, height, shadow, backendImageRoute}) {

    const defaultSize = 600;
    const [currentSlide, setCurrent] = useState(0);
    const [isHovering, setHovering] = useState(false);

    // Measured in Seconds
    const slideTimer = 5;
    let timer = null

    useEffect(() => {
        if(isHovering == false) {
            timer = setTimeout(() => {
                if(isHovering == false) rightClick();
            }, slideTimer * 1000)
        } 
    })

    const leftClick = () => {
        var nextSlide = currentSlide - 1;
        if(nextSlide < 0) {
            setCurrent(images.length - 1)
        } else {
            setCurrent(nextSlide)
        }
    }

    const rightClick = () => {
        var nextSlide = currentSlide + 1;
        if(nextSlide > images.length - 1) {
            setCurrent(0)
        } else {
            setCurrent(nextSlide)
        }
    }

    return (
        <div id='banner' onMouseEnter={()=> {
            setHovering(true)
            clearTimeout(timer)
        }} onMouseLeave={()=> setHovering(false)}>
            <div className='cover'></div>
            <div className='imgcont'>
                {images == null ? null: images.map((image, i) => {
                    return (
                        <Slide key={i} image={backendImageRoute ? backendImageRoute + '/ftp/' + image: image} height={height} index={i} currentSlide={currentSlide} />
                    )
                })}
            </div>
            <div className='dots'>
                {images == null ? null: images.map((image, i) => {
                    return <div style={{background: currentSlide == i ? 'white': 'rgba(255, 255, 255, .2)'}} key={i} className='dot' onClick={() => setCurrent(i)}></div>
                })}
            </div>
            <img onClick={leftClick} className='arrow' style={{left: 40}} src={'/logo_arrow_left.png'} />
            <img onClick={rightClick}  className='arrow right' style={{right: 40}} src={'/logo_arrow_left.png'} />
            <div className='content'>{children}</div>
            <style jsx>{`
                #banner {
                    float: left;
                    width: 100%;
                    position: relative;
                    overflow: hidden;
                    background: ${defaultBackground};
                    height: ${height}px;
                }
                .dots {
                    position: absolute;
                    min-width: 40px;
                    bottom: 14px;
                    left: 50%;
                    padding: 7px;
                    border-radius: 10px;
                    transform: translateX(-50%);
                    z-index: 12;
                }
                .dot {
                    float: left;
                    width: 12px;
                    height: 12px;
                    box-shadow: 0 0 2px rgba(20, 20,20,.4);
                    border-radius: 50%;
                    cursor: pointer;
                    transition: all .4s ease;
                    margin: 0 5px;
                }
                .arrow {
                    position: absolute;
                    width: 30px;
                    height: 30px;
                    top: 50%;
                    padding: 5px;
                    z-index: 12;
                    opacity: .4;
                    transition: all .3s ease;
                    backdrop-filter: blur(18px);
                    background: rgba(255, 255, 255, .6);
                    border-radius: 50%;
                    cursor: pointer;
                    transform: translateY(-50%);
                }
                .arrow:hover {
                    opacity: 1;
                }
                .right {
                    transform: translateY(-50%) rotateY(180deg);
                }
                .imgcont {
                    float: left;
                    height: 100%;
                    width: 100%;
                    opacity: ${isHovering ? '.94': 1};
                    transition: all .6s ease;
                    transform: translateX(-${currentSlide * 100}%);
                }
                .cover {
                    position: absolute;
                    top: 0;
                    left: 0;
                    transition: background .4s ease-in-out;
                    background: ${isHovering ? 'rgba(0,0,0,.4)': 'none'};
                    height: 100%;
                    width: 100%;
                    z-index: 10;
                }
                @media only screen and (max-width: 800px) {
                    #banner {
                        max-height: 300px;
                    }
                } 
                @media only screen and (max-width: 1100px) {
                }
            `}</style>
        </div>
    )
}
