import Link from 'next/link';
import Head from 'next/head';

const Navigation = ({title, links, logo}) => {
    return (
        <div id='nav'>
            <h1>{title}</h1>
            <ul>
                {links.map((link, i) => {
                    return <Link key={i} href={link.url}><li>{link.name}</li></Link>
                })}
            </ul>
            <style jsx>{`
                #nav {
                    float: left;
                    width: 100%;
                    height: 90px;
                    box-shadow: 0 0 2px rgba(23, 23, 23, .8);
                }
                ul {
                    float: left;
                    height: 90px;
                    position: relative;

                }
            `}</style>
        </div>
    )
}

const Header = ({title, currentpath}) => {
    return (
        <Head>
            <title>{title + ':' + currentpath}</title>
            <link href="https://fonts.googleapis.com/css2?family=Montserrat:wght@600&family=Open+Sans&family=Raleway&family=Roboto&display=swap" rel="stylesheet"/>
        </Head>
    )
}


function Layout({children, links, title}) {

    return (
        <div id='layout'>
            <Header title={title}/>
            <Navigation title={title} links={links} logo='/uplinkflat.png' />
            <div id="canvas">
                {children}
            </div>
            <style jsx global>{`
                #layout {
                    float: left;
                    width: 100%;
                }
                #canvas {
                    float: left;
                    width: 100%;
                }
                #__next {
                    float: left;
                    width: 100%;
                    height: 100%;
                    padding: 0;
                    margin: 0;
                }
                body {
                    float: left;
                    width: 100%;
                    height: 100%;
                    padding: 0;
                    margin: 0;
                }
                html {
                    float: left;
                    width: 100%;
                    height: 100%;
                    padding: 0;
                    margin: 0;
                }
            `}</style>
        </div>
    )
}

// Layout is out containing shell
// -> Canvas is inner container shell

export default Layout
