import { useState } from 'react'
import { baseConfig } from '../../services/restservice'
import Theme from '../../styles/theme'

function UploadImage({firstLoadImages}) {

    const [images, setImages] = useState(firstLoadImages)
    const [file, setFile] = useState(null)
    const [preview, setPreview] = useState(null)
    const [loadingImages, setLoadingImages] = useState(false)

    const uploadImageSingle = async(e) => {
        e.preventDefault()
        if(file == null) return 
        const formData = new FormData()
        formData.append("myFile", file)
        const uploadImage = await fetch(baseConfig.baseURL + '/uploadfile', {
            method: 'POST',
            body: formData
        })
        .then(response => {
            return response.json()
        })
        if(uploadImage) {
            setPreview(null)
            setFile(null)
            loadImages(e)
        }
    }

    const uploadImageMultiple = async(e) => {
        e.preventDefault()
        console.log(file)
        if(file == null) return 
        const formData = new FormData()

        for (let i = 0; i < file.length; i++) {
            console.log(file[i].uri)
            formData.append(`myImages[]`, {
                uri: file[i].uri,
                type: 'image/*',
                name: file[i].name
            })
        }

        console.log(formData)
        const newupload = await fetch('http://localhost:3600/uploadmultiple', {
            method: 'POST',
            body: formData
        })
        .then(response => {
            return response.json()
        })
        console.log(newupload)
        if(newupload) {
            setPreview(null)
            setFile(null)
            console.log(newupload)
            loadImages(e)
        }
    }

    const handleFileChangeSingle = (e) => {
        e.persist()
        setPreview(URL.createObjectURL(e.target.files[0]))
        setFile(e.target.files[0])
    }

    const handleFileChangeMulti = (e) => {
        e.persist()
        setPreview(URL.createObjectURL(e.target.files[0]))
        setFile(e.target.files[0])
    }

    const removeImage = async(e, image) => {
        e.preventDefault()
        const newremove = await fetch(baseConfig.baseURL + '/removeimage/' + image, {
            method: 'DELETE',
        }).then(response => {
            return response.status
        })
        if(newremove) {
            console.log(newremove)

        }
    }

    const loadImages = async(e) => {
        e.preventDefault()
        setLoadingImages(true)
        const getImages = await fetch('http://localhost:3600/getallimages', {
            method: 'GET',
        }).then (response => {
            return response.json()
        })
        if(getImages) {
            setImages(getImages.images)
        }
    }

    const doneLoading = (e) => {
        e.persist()
        setLoadingImages(false)
    }

    return (
        <div className='body'>
            <div id="left">
                <h2>Single Image Upload</h2>
                <div className='uploadform'>
                    <form onSubmit={uploadImageSingle}>
                        <div className='previewframe'><img className='preview' src={preview ? preview: '/imageplaceholder.png'} /></div>
                        <label htmlFor='fileid'>{file ? file.name: 'Select image'}</label>
                        <input id='fileid' type='file' accept='image/*' onChange={handleFileChangeSingle} required />
                        <button type='submit'>Upload Image</button>
                    </form>
                </div>
                <h2>Multiple Image Upload</h2>
                <div className='uploadform'>
                    <form onSubmit={uploadImageMultiple}>
                        <div className='previewframe'><img className='preview' src={preview ? preview: '/imageplaceholder.png'} /></div>
                        <label htmlFor='fileid'>{file ? file.name: 'Select images'}</label>
                        <input id='fileid' type='file' accept='image/*' onChange={handleFileChangeMulti} required multiple/>
                        <button type='submit'>Upload Images</button>
                    </form>
                </div>
            </div>
            <div className='images'>
                    <h1>My Images</h1>
                    <div className='imageholder'>
                        {images != null ? images.map((image, i) => {
                            if(i == images.length - 1) {
                                return (
                                    <div key={i} className='imgframe'>
                                        <div onClick={(e) => removeImage(e, image)} className='x'>x</div>
                                        <img onLoad={doneLoading} src={baseConfig.backendImages + image} />
                                    </div>
                                )
                            } else {
                                return (
                                    <div key={i} className='imgframe'>
                                        <div onClick={(e) => removeImage(e, image)} className='x'>x</div>
                                        <img src={baseConfig.backendImages + image} />
                                    </div>
                                )
                            }
                        }): <h2 onClick={loadImages}>Load Images</h2>}
                        <img className='loader' src='/loading_bubble.gif' />
                    </div>
                </div>
            <style jsx>{`
                .imageholder {
                    float: left;
                    width: 90%;
                    padding: 5px 5%;
                    min-height: 500px;
                    position: relative;
                    margin-top: 10px;
                    display: flex;
                    flex-wrap: wrap;
                    justify-content: center;
                    align-content: center;
                }
                .loader {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    width: 32px;
                    height: 32px;
                }
                .imgframe {
                    float: left;
                    width: 150px;
                    height: 120px;
                    margin: 8px;
                    overflow: hidden;
                    position: relative;
                    background: rgba(200,200,200,.2);
                    box-shadow: 0 0 1px ${Theme.colors.gunmetal};
                }
                .imgframe img {
                    position: absolute;
                    width: 100%;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                }
                .x {
                    position: absolute;
                    right: 10px;
                    top: 10px;
                    padding: 0 8px;
                    padding-bottom: 4px;
                    text-align: center;
                    font: 18px 'Roboto';
                    cursor: pointer;
                    opacity: .7;
                    transition: all .3s ease;
                    z-index: 999;
                    background: rgba(255,255,255,1);
                }
                .x:hover {
                    opacity: 1;
                }
                .images {
                    float: left;
                    width: 60%;
                }
                .images h1 {
                    float: left;
                    font: 30px 'Montserrat';
                    margin: 0;
                    margin-left: 30px;
                    padding: 10px;
                }
                .uploadform input {
                    display: none;
                }
                .uploadform label {
                    position: absolute;
                    top: 60px;
                    left: 20px;
                    padding: 10px;
                    cursor: pointer;
                    opacity: .7;
                    transition: all .3s ease;
                    font: 14px 'Roboto';
                }
                .uploadform label:hover {
                    opacity: 1;
                }
                .uploadform button {
                    position: absolute;
                    bottom: 90px;
                    left: 20px;
                    padding: 10px;
                    cursor: pointer;
                    opacity: .7;
                    transition: all .3s ease;
                    font: 14px 'Roboto';
                }
                .uploadform .previewframe {
                    position: absolute;
                    right: 20px;
                    top: 20px;
                    width: 300px;
                    max-height: 300px;
                    overflow: hidden;
                    display: flex;
                    justify-content: center;
                    align-content: center;
                }
                .uploadform .previewframe img {
                    float: left;
                    width: 100%;
                    height: auto;
                }
                .uploadform form {
                    float: left;
                    width: 100%;
                    height: 100%;
                }
                .uploadform {
                    float: left;
                    width: 100%;
                    position: relative;
                    min-height: 300px;
                }
                #left h2 {
                    float: left;
                    margin: 0;
                    width: 90%;
                    padding: 10px 5%;
                    box-shadow: 0 0 1px ${Theme.colors.charcoal};
                    font: 22px 'Raleway';
                }
                #left {
                    float: left;
                    width: 40%;
                    height: 100%;
                }
                .body {
                    float: left;
                    width: 100%;
                    height: 100%;
                    margin-top: 90px;
                }
            `}</style>
        </div>
    )
}

UploadImage.getInitialProps = async(e) => {

    const getimg = await fetch(baseConfig.baseURL + '/getallimages', {
        method: 'GET',
    }).then (response => {
        return response.json()
    })
    return {firstLoadImages: getimg.images}
}

export default UploadImage
